export const fetchStats = async () => {
  try {
    const res = await fetch("http://localhost/api/Statspage.php");
    if (!response.ok) {
      throw new Error(`Error fetching stats: ${response.status}`);
    }
    const data = await response.json();
    return data;
  } catch (error) {
    console.error(error);
    throw error;
  }
};
