export const fetchUsers = async () => {
  try {
    const response = await fetch("http://localhost/Servidor/JS/React/control-insulina/api/Users.php");
    if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
    return await response.json();
  } catch (error) {
    console.error("Error fetching users:", error);
    return [];
  }
};

export const fetchStats = async () => {
  try {
    const res = await fetch("http://localhost/Servidor/JS/React/control-insulina/api/Stats.php");
    if (!res.ok) {
      throw new Error(`Error fetching stats: ${res.status}`);
    }
    const data = await res.json();
    return data;
  } catch (error) {
    console.error(error);
    throw error;
  }
};
export const addUser = async (user) => {
  try {
    const res = await fetch("http://localhost/Servidor/JS/React/control-insulina/api/Users.php", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(user),
    });
    if (!res.ok) {
      throw new Error(`Error adding user: ${res.status}`);
    }
    const data = await res.json();
    return data;
  } catch (error) {
    console.error(error);
    throw error;
  }
};
